import React, { useState, useEffect, useRef } from 'react';
import { Play, Plus, Zap, Sword, FlaskConical, Link, Users, Home } from 'lucide-react';
import { API_URL, SOCKET_URL } from './config';
import io from 'socket.io-client';
import './App.css';

const App = () => {
    const [socket, setSocket] = useState(null);
    const [authenticated, setAuthenticated] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [nodes, setNodes] = useState([]);
    const [selectedNode, setSelectedNode] = useState(null);
    const [armies, setArmies] = useState([]);
    const [technologies, setTechnologies] = useState([]);
    const [view, setView] = useState('login');
    const canvasRef = useRef(null);
    const socketRef = useRef(null);
    const [nodeCreationPending, setNodeCreationPending] = useState(false);
    const [pendingNodeAssociations, setPendingNodeAssociations] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [matchedNodes, setMatchedNodes] = useState([]);

    useEffect(() => {
        if (socketRef.current) {
            return;
        }

        const newSocket = io('http://192.168.1.96:5000', {
            transports: ['websocket', 'polling'],
            reconnection: true,
            reconnectionDelay: 1000,
            reconnectionDelayMax: 5000,
            reconnectionAttempts: 5,
            timeout: 20000,
            autoConnect: true
        });

        newSocket.on('connect', () => {
            console.log('WebSocket è¿æ¥æˆåŠŸ:', newSocket.id);
        });

        newSocket.on('connect_error', (error) => {
            console.error('WebSocket è¿æ¥é”™è¯¯:', error);
        });

        newSocket.on('disconnect', (reason) => {
            console.log('WebSocket æ–­å¼€è¿æ¥:', reason);
        });

        newSocket.on('authenticated', (data) => {
            setAuthenticated(true);
            setView('game');
            newSocket.emit('getGameState');
        });

        newSocket.on('gameState', (data) => {
            setNodes(data.nodes || []);
            setArmies(data.armies || []);
        });

        newSocket.on('nodeCreated', (node) => {
            setNodes(prev => [...prev, node]);
        });

        newSocket.on('armyProduced', (data) => {
            setArmies(prev => {
                const existing = prev.find(a => a.nodeId === data.nodeId && a.type === data.type);
                if (existing) {
                    return prev.map(a => 
                        a.nodeId === data.nodeId && a.type === data.type 
                            ? { ...a, count: a.count + data.count }
                            : a
                    );
                }
                return [...prev, data.army];
            });
        });

        newSocket.on('techUpgraded', (tech) => {
            setTechnologies(prev => {
                const existing = prev.find(t => t.techId === tech.techId);
                if (existing) {
                    return prev.map(t => t.techId === tech.techId ? tech : t);
                }
                return [...prev, tech];
            });
        });

        newSocket.on('resourcesUpdated', () => {
            newSocket.emit('getGameState');
        });

        socketRef.current = newSocket;
        setSocket(newSocket);

        return () => {
            if (socketRef.current) {
                socketRef.current.removeAllListeners();
                socketRef.current.close();
                socketRef.current = null;
            }
        };
    }, []);

    useEffect(() => {
        if (canvasRef.current && nodes.length > 0) {
            drawNetwork();
        }
    }, [nodes, selectedNode]);

    const drawNetwork = () => {
        const canvas = canvasRef.current;
        if (!canvas) return;

        const ctx = canvas.getContext('2d');
        const width = canvas.width;
        const height = canvas.height;

        ctx.clearRect(0, 0, width, height);

        // ç»˜åˆ¶è¿æ¥çº¿
        ctx.strokeStyle = '#4b5563';
        ctx.lineWidth = 2;
        nodes.forEach(node => {
            node.connectedNodes?.forEach(connectedId => {
                const connectedNode = nodes.find(n => n._id === connectedId);
                if (connectedNode) {
                    ctx.beginPath();
                    ctx.moveTo(node.position.x, node.position.y);
                    ctx.lineTo(connectedNode.position.x, connectedNode.position.y);
                    ctx.stroke();
                }
            });
        });

        // ç»˜åˆ¶èŠ‚ç‚¹
        nodes.forEach(node => {
            const prosperity = node.prosperity || 100;
            const radius = 15 + (prosperity / 50);

            // èŠ‚ç‚¹å…‰æ™•
            const gradient = ctx.createRadialGradient(
                node.position.x, node.position.y, 0,
                node.position.x, node.position.y, radius
            );
            gradient.addColorStop(0, `rgba(59, 130, 246, ${prosperity / 200})`);
            gradient.addColorStop(1, 'rgba(59, 130, 246, 0)');

            ctx.fillStyle = gradient;
            ctx.beginPath();
            ctx.arc(node.position.x, node.position.y, radius * 1.5, 0, Math.PI * 2);
            ctx.fill();

            // èŠ‚ç‚¹ä¸»ä½“
            ctx.fillStyle = selectedNode?._id === node._id ? '#3b82f6' : '#6366f1';
            ctx.beginPath();
            ctx.arc(node.position.x, node.position.y, radius, 0, Math.PI * 2);
            ctx.fill();

            // èŠ‚ç‚¹è¾¹æ¡†
            ctx.strokeStyle = '#1e40af';
            ctx.lineWidth = 2;
            ctx.stroke();

            // èŠ‚ç‚¹åç§°
            ctx.fillStyle = '#ffffff';
            ctx.font = '12px sans-serif';
            ctx.textAlign = 'center';
            ctx.fillText(node.name, node.position.x, node.position.y - radius - 5);
        });
    };

    const handleLogin = async (isRegister = false) => {
        try {
            const response = await fetch(`http://192.168.1.96:5000/api/${isRegister ? 'register' : 'login'}`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, password })
            });

            const data = await response.json();
            if (response.ok) {
                localStorage.setItem('token', data.token);
                socket.emit('authenticate', data.token);
            } else {
                alert(data.error);
            }
        } catch (error) {
            alert('è¿æ¥å¤±è´¥: ' + error.message);
        }
    };

    const handleCreateNode = () => {
        const name = prompt('è¾“å…¥èŠ‚ç‚¹åç§°:');
        if (!name) return;

        const x = Math.random() * 700 + 50;
        const y = Math.random() * 400 + 50;

        socket.emit('createNode', {
            name,
            position: { x, y }
        });
    };

    const handleProduceArmy = (type) => {
        if (!selectedNode) {
            alert('è¯·å…ˆé€‰æ‹©ä¸€ä¸ªèŠ‚ç‚¹');
            return;
        }

        const count = parseInt(prompt(`ç”Ÿäº§${type}æ•°é‡:`));
        if (!count || count <= 0) return;

        socket.emit('produceArmy', {
            nodeId: selectedNode._id,
            type,
            count
        });
    };

    const handleUpgradeTech = (techId) => {
        socket.emit('upgradeTech', { techId });
    };

    const handleCanvasClick = (e) => {
        const rect = canvasRef.current.getBoundingClientRect();
        const x = e.clientX - rect.left;
        const y = e.clientY - rect.top;

        const clickedNode = nodes.find(node => {
            const dx = node.position.x - x;
            const dy = node.position.y - y;
            const distance = Math.sqrt(dx * dx + dy * dy);
            return distance < 20;
        });

        setSelectedNode(clickedNode || null);
    };

    if (view === 'login') {
        return (
            <div className="login-container">
                <div className="login-box">
                    <div className="login-header">
                        <h1 className="login-title">ç­–ç•¥ç»è¥æ¸¸æˆ</h1>
                        <p className="login-subtitle">å¤šèŠ‚ç‚¹ç½‘ç»œæˆ˜ç•¥ç³»ç»Ÿ</p>
                    </div>

                    <div className="login-form">
                        <input
                            type="text"
                            placeholder="ç”¨æˆ·å"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            className="login-input"
                        />
                        <input
                            type="password"
                            placeholder="å¯†ç "
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="login-input"
                        />
                        <div className="login-buttons">
                            <button
                                onClick={() => handleLogin(false)}
                                className="btn btn-primary"
                            >
                                ç™»å½•
                            </button>
                            <button
                                onClick={() => handleLogin(true)}
                                className="btn btn-secondary"
                            >
                                æ³¨å†Œ
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="game-container">
            <div className="game-content">
                {/* å¤´éƒ¨ */}
                <div className="header">
                    <div className="header-content">
                        <h1 className="header-title">
                            <Home className="icon" />
                            å¤šèŠ‚ç‚¹ç­–ç•¥ç³»ç»Ÿ
                        </h1>
                        <div className="header-buttons">
                            <button
                                onClick={() => setView('game')}
                                className="btn btn-primary"
                            >
                                åœ°å›¾
                            </button>
                            <button
                                onClick={() => setView('tech')}
                                className="btn btn-secondary"
                            >
                                ç§‘æŠ€
                            </button>
                        </div>
                    </div>
                </div>

                {view === 'game' && (
                    <div className="game-layout">
                        {/* ä¸»åœ°å›¾åŒºåŸŸ */}
                        <div className="map-section">
                            <div className="section-header">
                                <h2 className="section-title">èŠ‚ç‚¹ç½‘ç»œå›¾</h2>
                                <button
                                    onClick={handleCreateNode}
                                    className="btn btn-success"
                                >
                                    <Plus className="icon-small" />
                                    åˆ›å»ºèŠ‚ç‚¹
                                </button>
                            </div>
                            <canvas
                                ref={canvasRef}
                                width={800}
                                height={500}
                                onClick={handleCanvasClick}
                                className="game-canvas"
                            />
                        </div>

                        {/* ä¾§è¾¹æ  */}
                        <div className="sidebar">
                            {/* èŠ‚ç‚¹ä¿¡æ¯ */}
                            <div className="card">
                                <h3 className="card-title">
                                    <Zap className="icon-small icon-yellow" />
                                    {selectedNode ? selectedNode.name : 'æœªé€‰æ‹©èŠ‚ç‚¹'}
                                </h3>

                                {selectedNode && (
                                    <div className="card-content">
                                        <div className="info-box">
                                            <p className="info-label">ç¹è£åº¦</p>
                                            <div className="progress-container">
                                                <div className="progress-bar">
                                                    <div
                                                        className="progress-fill"
                                                        style={{ width: `${Math.min(selectedNode.prosperity || 0, 100)}%` }}
                                                    />
                                                </div>
                                                <span className="progress-value">{Math.round(selectedNode.prosperity || 0)}</span>
                                            </div>
                                        </div>

                                        <div className="info-box">
                                            <p className="info-label">èµ„æº</p>
                                            <div className="resource-list">
                                                <div className="resource-item">
                                                    <span>é£Ÿç‰©:</span>
                                                    <span className="resource-value">{Math.round(selectedNode.resources?.food || 0)}</span>
                                                </div>
                                                <div className="resource-item">
                                                    <span>é‡‘å±:</span>
                                                    <span className="resource-value">{Math.round(selectedNode.resources?.metal || 0)}</span>
                                                </div>
                                                <div className="resource-item">
                                                    <span>èƒ½é‡:</span>
                                                    <span className="resource-value">{Math.round(selectedNode.resources?.energy || 0)}</span>
                                                </div>
                                            </div>
                                        </div>

                                        <div className="info-box">
                                            <p className="info-label">ç”Ÿäº§ç‡</p>
                                            <div className="resource-list">
                                                <div className="resource-item">
                                                    <span>é£Ÿç‰©/åˆ†:</span>
                                                    <span className="resource-value">{selectedNode.productionRates?.food || 0}</span>
                                                </div>
                                                <div className="resource-item">
                                                    <span>é‡‘å±/åˆ†:</span>
                                                    <span className="resource-value">{selectedNode.productionRates?.metal || 0}</span>
                                                </div>
                                                <div className="resource-item">
                                                    <span>èƒ½é‡/åˆ†:</span>
                                                    <span className="resource-value">{selectedNode.productionRates?.energy || 0}</span>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                )}
                            </div>

                            {/* å†›é˜Ÿç”Ÿäº§ */}
                            <div className="card">
                                <h3 className="card-title">
                                    <Sword className="icon-small icon-red" />
                                    å†›é˜Ÿç”Ÿäº§
                                </h3>

                                <div className="army-grid">
                                    {[
                                        { type: 'infantry', label: 'æ­¥å…µ' },
                                        { type: 'cavalry', label: 'éª‘å…µ' },
                                        { type: 'archer', label: 'å¼“ç®­æ‰‹' },
                                        { type: 'siege', label: 'æ”»åŸå™¨' }
                                    ].map(army => (
                                        <button
                                            key={army.type}
                                            onClick={() => handleProduceArmy(army.type)}
                                            disabled={!selectedNode}
                                            className="btn btn-danger"
                                        >
                                            {army.label}
                                        </button>
                                    ))}
                                </div>

                                {selectedNode && (
                                    <div className="army-list">
                                        {armies
                                            .filter(a => a.nodeId === selectedNode._id)
                                            .map((army, idx) => (
                                                <div key={idx} className="army-item">
                                                    <span className="army-name">
                                                        {army.type === 'infantry' ? 'æ­¥å…µ' :
                                                         army.type === 'cavalry' ? 'éª‘å…µ' :
                                                         army.type === 'archer' ? 'å¼“ç®­æ‰‹' : 'æ”»åŸå™¨'}
                                                    </span>
                                                    <span className="army-count">Ã—{army.count}</span>
                                                </div>
                                            ))}
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                )}

                {view === 'tech' && (
                    <div className="tech-section">
                        <h2 className="section-title-large">
                            <FlaskConical className="icon" />
                            ç§‘æŠ€æ ‘
                        </h2>

                        <div className="tech-grid">
                            {[
                                { id: 'agriculture', name: 'å†œä¸šç§‘æŠ€', icon: 'ğŸŒ¾', color: 'green' },
                                { id: 'metallurgy', name: 'å†¶é‡‘å­¦', icon: 'âš’ï¸', color: 'orange' },
                                { id: 'warfare', name: 'å†›äº‹å­¦', icon: 'âš”ï¸', color: 'red' },
                                { id: 'engineering', name: 'å·¥ç¨‹å­¦', icon: 'ğŸ—ï¸', color: 'blue' }
                            ].map(tech => {
                                const userTech = technologies.find(t => t.techId === tech.id);
                                const level = userTech?.level || 0;

                                return (
                                    <div key={tech.id} className="tech-card">
                                        <div className="tech-header">
                                            <div className="tech-info">
                                                <span className="tech-icon">{tech.icon}</span>
                                                <div>
                                                    <h3 className="tech-name">{tech.name}</h3>
                                                    <p className="tech-level">ç­‰çº§ {level}</p>
                                                </div>
                                            </div>
                                        </div>

                                        <button
                                            onClick={() => handleUpgradeTech(tech.id)}
                                            className={`btn btn-${tech.color}`}
                                        >
                                            å‡çº§åˆ° {level + 1} çº§
                                        </button>

                                        {userTech && (
                                            <div className="tech-effects">
                                                <p>ç¹è£åº¦åŠ æˆ: +{(userTech.effects.prosperityBonus * 100).toFixed(0)}%</p>
                                                <p>ç”Ÿäº§åŠ æˆ: +{(userTech.effects.productionBonus * 100).toFixed(0)}%</p>
                                                <p>å†›äº‹åŠ æˆ: +{(userTech.effects.militaryBonus * 100).toFixed(0)}%</p>
                                            </div>
                                        )}
                                    </div>
                                );
                            })}
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default App;
