// frontend/src/App.js
import React, { useState, useEffect, useRef } from 'react';
import { API_URL, SOCKET_URL } from './config';
import io from 'socket.io-client';

const App = () => {
  // ... other state variables

  const [nodeCreationPending, setNodeCreationPending] = useState(false);
  const [pendingNodeAssociations, setPendingNodeAssociations] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [matchedNodes, setMatchedNodes] = useState([]);

  useEffect(() => {
    // ... other useEffect hooks

    socket.on('nodeCreated', (node) => {
      setNodes((prev) => [...prev, node]);
    });

    socket.on('nodeAssociationApproved', (data) => {
      setNodes((prev) =>
        prev.map((n) =>
          n._id === data.nodeId ? { ...n, status: 'approved' } : n
        )
      );
      setPendingNodeAssociations((prev) =>
        prev.filter((a) => a.nodeId !== data.nodeId)
      );
    });

    socket.on('nodeAssociationRejected', (data) => {
      setNodes((prev) =>
        prev.map((n) =>
          n._id === data.nodeId ? { ...n, status: 'rejected' } : n
        )
      );
      setPendingNodeAssociations((prev) =>
        prev.filter((a) => a.nodeId !== data.nodeId)
    );
    });
  }, [socket]);

  const handleCreateNode = async () => {
    try {
      const name = prompt('输入节点名称:');
      const description = prompt('输入节点简介:');
      if (!name || !description) return;

      setNodeCreationPending(true);

      const x = Math.random() * 700 + 50;
      const y = Math.random() * 400 + 50;

      socket.emit('createNode', {
        name,
        description,
        position: { x, y },
      });
    } catch (error) {
      alert('创建节点失败: ' + error.message);
    } finally {
      setNodeCreationPending(false);
    }
  };

  const handleSearchNodes = () => {
    const matchedNodes = nodes.filter((node) =>
      (node.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        node.description.toLowerCase().includes(searchQuery.toLowerCase())) &&
      node.status === 'approved'
    );
    setMatchedNodes(matchedNodes);
  };

  const handleAssociateNode = (nodeId) => {
    socket.emit('associateNode', { nodeId });
    setPendingNodeAssociations((prev) => [...prev, { nodeId }]);
  };

  const handleApproveAssociation = (nodeId, isParent) => {
    socket.emit('approveNodeAssociation', { nodeId, isParent });
  };

  const handleRejectAssociation = (nodeId) => {
    socket.emit('rejectNodeAssociation', { nodeId });
  };

  return (
    <div className="game-container">
      {/* ... other JSX */}

      <div className="node-creation-section">
        <h3 className="section-title">创建新节点</h3>
        <button
          onClick={handleCreateNode}
          disabled={nodeCreationPending}
          className="btn btn-success"
        >
          创建节点
        </button>
        {nodeCreationPending && <p>节点创建中...</p>}
      </div>

      <div className="node-association-section">
        <h3 className="section-title">关联节点</h3>
        <div className="search-container">
          <input
            type="text"
            placeholder="搜索节点"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="search-input"
          />
          <button onClick={handleSearchNodes} className="btn btn-primary">
            搜索
          </button>
        </div>
        <div className="matched-nodes-container">
          {matchedNodes.map((node) => (
            <div key={node._id} className="node-card">
              <h4 className="node-name">{node.name}</h4>
              <p className="node-description">{node.description}</p>
              {pendingNodeAssociations.find((a) => a.nodeId === node._id) ? (
                <div>
                  <p>正在等待管理员审批...</p>
                  <button
                    onClick={() => handleRejectAssociation(node._id)}
                    className="btn btn-danger"
                  >
                    取消关联
                  </button>
                </div>
              ) : (
                <button
                  onClick={() => handleAssociateNode(node._id)}
                  className="btn btn-primary"
                >
                  关联节点
                </button>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* ... other JSX */}
    </div>
  );
};

export default App;
